nginx:
add_header Content-Security-Policy "default-src 'self'; script-src 'self' 'unsafe-inline' 'unsafe-eval' https://cdn.jsdelivr.net https://unpkg.com; style-src 'self' 'unsafe-inline' https://fonts.googleapis.com; img-src 'self' data: https: blob:; connect-src 'self' https://api.gpzh.ch wss://localhost:* ws://localhost:*; font-src 'self' https://fonts.googleapis.com https://fonts.gstatic.com data:; object-src 'none'; media-src 'self' https://media.gpzh.ch; frame-src 'self' https://www.youtube.com https://player.vimeo.com https://maps.google.com; worker-src 'self' blob:; child-src 'self' blob:; base-uri 'self'; form-action 'self' https://forms.gpzh.ch; frame-ancestors 'none'; report-uri https://security.gpzh.ch/csp-report; report-to csp-endpoint" always;
add_header X-Content-Type-Options "nosniff" always;
add_header X-Frame-Options "DENY" always;
add_header X-XSS-Protection "1; mode=block" always;
add_header Referrer-Policy "strict-origin-when-cross-origin" always;
add_header Strict-Transport-Security "max-age=31536000; includeSubDomains; preload" always;
add_header Permissions-Policy "geolocation=(self), microphone=(), camera=()" always;
add_header Cross-Origin-Embedder-Policy "require-corp" always;
add_header Cross-Origin-Opener-Policy "same-origin" always;
add_header Cross-Origin-Resource-Policy "same-origin" always;
